import java.util.Scanner;

public class Solution {
    private static final String defaultSchedule = "";
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int T = scanner.nextInt();
        for (int iT = 1; iT <= T; iT++) {

            StringBuilder ans = new StringBuilder();
            String P1 = defaultSchedule;
            String P2 = defaultSchedule;
            int numberOfTask = scanner.nextInt();
            for (int i = 0; i < numberOfTask; i++) {
                int start = scanner.nextInt();
                int end = scanner.nextInt();
                String substring = P1.substring(start, end);
                if (substring.contains("X")) {
                    substring = P2.substring(start, end);
                    if (substring.contains("X")) {
                        ans = new StringBuilder("IMPOSSIBLE");
                        break;
                    } else {
                        P2 = addTask(P2, start, end);
                        ans.append("J");
                    }
                } else {
                    P1 = addTask(P1, start, end);
                    ans.append("C");
                }

            }
            System.out.println(String.format("Case #%d: %s",iT,ans));
        }
        scanner.close();
    }

    private static String addTask(String defaultSchedule, int start, int end) {
        String x = defaultSchedule.substring(start, end).replace("0", "X");
        return defaultSchedule.substring(0,start)+x+defaultSchedule.substring(end);
    }

}
